import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  all:      [],
  selected: null,
};

export const slice = createSlice({
  name:     'category',
  initialState,
  reducers: {
    select: (state, { payload }) => {
      state.selected = payload;
    },

    unselect: (state) => {
      state.selected = null;
    },
  },
});

export const { select, unselect } = slice.actions;

export default slice.reducer;
