import { io } from 'socket.io-client';
import { fetchTeams } from './reducers/quiz';

let ws;

const connect = lobby => ws.emit('SCOREBOARD_CONNECT', { lobby });

const socket = (dispatch, state, history) => {
  if (ws === undefined) {
    ws = io('ws://localhost:3000', { reconnectionDelayMax: 10000, withCredentials: true });
  }

  ws.off('message').on('message', ({ type, payload }) => {
    console.log('message')
    switch (type) {
      case 'TEAMS_CHANGED':
        dispatch(fetchTeams());
        break;
      case 'TEAM_REMOVED':
        dispatch(fetchTeams());
        break;
      case '':
        // TODO: Implement message.
        break;
      default:
        console.log('Socket.io message:', type, payload);
    }
  });
};

export { connect, socket };
