import WinnerGraphBar from './WinnerGraphBar';
import { useSelector } from 'react-redux';

const WinnerGraph = () => {
  const results = useSelector(({ quiz }) => quiz.results);

  const calculatePercentage = (maxScore, ownScore) => Math.round((ownScore / maxScore) * 100 / 1.5);

  let orderedWinners;

  if (results.length >= 3) {
    const [first, second, third] = [...results];

    orderedWinners = [
      { ...second, percentage: calculatePercentage(first.score, second.score) },
      { ...first, percentage: 100 },
      { ...third, percentage: calculatePercentage(first.score, third.score) },
    ];
  } else if (results.length === 2) {
    const [first, second] = [...results];

    orderedWinners = [
      { ...second, percentage: calculatePercentage(first.score, second.score) },
      { ...first, percentage: 100 },
    ];
  } else if (results.length === 1) {
    const [first] = [...results];

    orderedWinners = [
      { ...first, percentage: 100 },
    ];
  }


  return (
    <div className="m-winner-graph">
      {orderedWinners.map((winner, index) => (
        <WinnerGraphBar
          name={winner._id}
          score={winner.score}
          place={index + 1 === 1 ? 2 : index + 1 === 2 ? 1 : 3}
          percentage={winner.percentage}
          key={winner._id}
        />
      ))}
    </div>
  );
};

export default WinnerGraph;
