import { useDispatch, useSelector } from 'react-redux';
import { Route, Switch } from 'react-router';
import { useHistory } from 'react-router-dom';
import { socket } from '../socket';
import Answer from './pages/Answer';
import LobbyInfo from './pages/LobbyInfo';
import Question from './pages/Question';
import Result from './pages/Result';

const App = () => {
  const dispatch = useDispatch();
  const state    = useSelector(state => state);
  const history  = useHistory();
  socket(dispatch, state, history);

  return (
    <div className="a-container a-container--gutter py-10">
      <Switch>
        <Route exact path="/lobby/:lobbyId">
          <LobbyInfo />
        </Route>
        <Route exact path="/waiting">
          <Question />
        </Route>
        <Route exact path="/lobby/:lobbyId/question">
          <Question />
        </Route>
        <Route exact path="/lobby/:lobbyId/answer">
          <Answer />
        </Route>
        <Route exact path="/lobby/:lobbyId/result">
          <Result />
        </Route>
      </Switch>
    </div>
  );
};

export default App;
