import WinnerGraphBar from './WinnerGraphBar';

const WinnerGraph = () => {
  const winners = [
    {
      _id:              'Team een',
      score:            24,
      questionsCorrect: 11,
    },
    {
      _id:              'Team twee',
      score:            18,
      questionsCorrect: 7,
    },
    {
      _id:              'Team drie',
      score:            16,
      questionsCorrect: 6,
    },
  ];

  const [first, second, third] = [...winners];
  const orderedWinners         = [
    { ...second, place: 2 },
    { ...first, place: 1 },
    { ...third, place: 3 },
  ];

  return (
    <div className="m-winner-graph">
      {orderedWinners.map(winner => (
        <WinnerGraphBar
          name={winner._id}
          score={winner.score}
          questionsRight={winner.questionsRight}
          place={winner.place}
        />
      ))}
    </div>
  );
};

export default WinnerGraph;
