import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';

const initialState = {
  lobbyId: 'a',
  teams:   []
};

export const fetchTeams = createAsyncThunk('category/fetchTeamsStatus', async ({ lobbyId }) => {
  const teams = await axios.get(`${process.env.REACT_APP_API_URI}/lobbies/${lobbyId}/teams`);
  return teams.data;
});

export const slice = createSlice({
  name:          'quiz',
  initialState,
  reducers:      {
    example: () => {
    },
  },
  extraReducers: {
    [fetchTeams.fulfilled]: (state, { payload }) => {
      state.teams = payload;
    },
  }
});

export const { example } = slice.actions;

export default slice.reducer;
