import { io } from 'socket.io-client';

let ws;

const connect = lobbyId => {
  ws.emit('MASTER_CONNECT', lobbyId);
};

const socket = (dispatch, history) => {
  if (ws === undefined) {
    ws = io('ws://localhost:3000', { reconnectionDelayMax: 10000, withCredentials: true });
  }

  ws.on('message', type => {
    switch (type) {
      case 'MASTER_NEW_ROUND':

        console.log("HIT master new round");

        history.push('/question');
        break;
      default:
        console.log(type);
    }
  });
};

export { connect, socket };
