import { io } from 'socket.io-client';

let ws;

const sendMessage = (type, data) => {
  ws.emit(type, data);
};

const socket = dispatch => {
  if (ws === undefined) {
    ws = io('ws://localhost:3000', { reconnectionDelayMax: 10000, withCredentials: true });
  }

  ws.on('message', message => {
    console.log(message);
  });
};

export { sendMessage, socket };
