import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

const initialState = {
  teamName:  '',
  lobbyCode: ''
};

const joinGame = createAsyncThunk(
  'quiz/joinGameStatus',
  async (lobbyCode, teamName) => {
    console.log(lobbyCode, teamName);
  }
);

export const slice = createSlice({
  name:          'quiz',
  initialState,
  reducers:      {},
  extraReducers: {
    [joinGame.pending]:   state => {
      state.loading = true;
    },
    [joinGame.fulfilled]: (state, { payload }) => {
      state.loading  = false;
      state.entities = payload;
    },
    [joinGame.rejected]:  state => {
      state.loading = false;
    },
  }
});

export const {} = slice.actions;

export default slice.reducer;
