import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

const initialState = {
  loading:   false,
  teamName:  '',
  lobbyCode: ''
};

export const joinGame = createAsyncThunk(
  'quiz/joinGameStatus',
  async () => {
    await new Promise(resolve => setTimeout(resolve, 1000));
  }
);

export const slice = createSlice({
  name:          'quiz',
  initialState,
  reducers:      {},
  extraReducers: {
    [joinGame.pending]:   state => { state.loading = true },
    [joinGame.fulfilled]: state => { state.loading = false },
    [joinGame.rejected]:  state => { state.loading = false },
  },
});

export const {} = slice.actions;

export default slice.reducer;
