import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

const initialState = {
  loading:   false,
  teamName:  'team naam',
  lobbyCode: 'lobby code'
};

export const joinGame = createAsyncThunk(
  'quiz/joinGameStatus',
  async ({ roomCode, teamName }) => {
    setTimeout(() => {
      console.log(roomCode, teamName);
    }, 2000)
  }
);

export const slice = createSlice({
  name:          'quiz',
  initialState,
  reducers:      {},
  extraReducers: {
    [joinGame.pending]:   state => { state.loading = false },
    [joinGame.fulfilled]: state => { state.loading = false },
    [joinGame.rejected]:  state => { state.loading = false },
  },
});

export const {} = slice.actions;

export default slice.reducer;
