import TextInput from './TextInput';
import Button from './Button';
import { useDispatch, useSelector } from 'react-redux';
import { joinGame } from '@reducers/quiz-reducer';

const LoginForm = (props) => {

  const dispatch  = useDispatch();
  const teamName  = useSelector(state => state.quiz.teamName);
  const lobbyCode = useSelector(state => state.quiz.lobbyCode);
  const loading = useSelector(state => state.quiz.loading);

  const submit = event => {
    event.preventDefault();
    dispatch(joinGame({ lobbyCode, teamName }));
  };

  return (
    <form onSubmit={submit}>
      loading ? (<h1>Aan het laden</h1>)
      <TextInput label="Code" value="code" />
      <TextInput label="Team naam" value="team" />

      <div className="mx-2">
        <Button type="submit">Meedoen</Button>
      </div>
    </form>
  );
};

export default LoginForm;
