import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { joinGame, setLobbyId, setTeamName } from '../reducers/quiz';
import { sendMessage } from '../socket';
import Button from './Button';
import TextInput from './TextInput';

const LoginForm = () => {
  const dispatch          = useDispatch();
  const teamName          = useSelector(state => state.quiz.teamName);
  const lobbyId           = useSelector(state => state.quiz.lobbyId);
  const loading           = useSelector(state => state.quiz.loading);
  const history           = useHistory();
  const [error, setError] = useState('');

  const submit = async event => {
    event.preventDefault();

    const { payload } = await dispatch(joinGame({ lobbyId, teamName }));

    console.log(payload);

    switch (payload.status) {
      case 400:
        setError('Er bestaat geen game met deze code.');
        break;
      case 403:
        setError('Team naam is al gekozen, kies een andere.');
        break;
      default:
        sendMessage('TEAM_CONNECT', { lobby: payload.data });
        history.push('/question');
    }

  };

  const valid = lobbyId !== '' && teamName !== '';

  return (
    <form onSubmit={submit}>
      {loading && <h1>Aan het laden...</h1>}

      <h2>{error}</h2>

      <TextInput label="Code" value={lobbyId} onChange={e => dispatch(setLobbyId(e.target.value))} />
      <TextInput label="Team naam" value={teamName} onChange={e => dispatch(setTeamName(e.target.value))} />

      <Button disabled={!valid} type="submit">Meedoen</Button>

    </form>
  );
};

export default LoginForm;
