import {useEffect} from 'react';
import {getQuestion} from '../../reducers/quiz';
import {useDispatch, useSelector} from "react-redux";

const Question = () => {
  const dispatch = useDispatch();
  const question = useSelector(({ quiz }) => quiz.question);

  useEffect(() => {
    dispatch(getQuestion('a'));

  }, [dispatch]);

  return (
          <div className="a-container">
            vraag: {question}
          </div>
  );
};

export default Question;
