import { io } from 'socket.io-client';

let ws;

const connect = lobbyId => {
  ws.emit('TEAM_CONNECT', { lobby: lobbyId });
};

const socket = (dispatch, state, history) => {
  if (ws === undefined) {
    ws = io('ws://localhost:3000', { reconnectionDelayMax: 10000, withCredentials: true });
  }

  ws.on('message', ({ type, payload }) => {
    switch (type) {
      case 'TEAM_REMOVED':
        console.log(payload);
        if (state.quiz.teamName === '') {
          history.push('/');
        }
    }
  });
};

export { connect, socket };
