import TextInput from './TextInput';
import Button from './Button';
import { useDispatch, useSelector } from 'react-redux';
import { joinGame, setLobbyId, setTeamName } from '../reducers/quiz';

const LoginForm = () => {
  const dispatch  = useDispatch();
  const teamName  = useSelector(state => state.quiz.teamName);
  const lobbyId = useSelector(state => state.quiz.lobbyId);
  const loading   = useSelector(state => state.quiz.loading);

  const submit = event => {
    event.preventDefault();
    dispatch(joinGame({lobbyId, teamName}));
  };

  const valid = lobbyId !== '' && teamName !== '';

  return (
    <form onSubmit={submit}>
      {loading && <h1>Aan het laden...</h1>}
      <TextInput label="Code" value={lobbyId} onChange={e => dispatch(setLobbyId(e.target.value))}/>
      <TextInput label="Team naam" value={teamName} onChange={e => dispatch(setTeamName(e.target.value))}/>

      <Button disabled={!valid} type="submit">Meedoen</Button>

    </form>
  );
};

export default LoginForm;
