import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';
import { BASE_URL } from '../api';

const initialState = {
  loading:  false,
  teamName: '',
  lobbyId:  ''
};


export const joinGame = createAsyncThunk(
  'quiz/joinGameStatus',
  async ({ lobbyId, teamName }) => {
    console.log(lobbyId)
    const response = await axios.post(`${BASE_URL}/lobbies/${lobbyId}/teams`, {
      teamName: teamName
    }).catch((error) => {
      return {'data': error.response.data, 'status': error.response.status};
    });

    return {'data': response.data, 'status': response.status};
  }
);

export const slice = createSlice({
  name:          'quiz',
  initialState,
  reducers:      {
    setLobbyId:  (state, { payload }) => {
      state.lobbyId = payload;
    },
    setTeamName: (state, { payload }) => {
      state.teamName = payload;
    }
  },
  extraReducers: {
    [joinGame.pending]:   state => {
      state.loading = true;
    },
    [joinGame.fulfilled]: state => {
      state.loading = false;
    },
    [joinGame.rejected]:  state => {
      state.loading = false;
    },
  },
});

export const { setLobbyId, setTeamName } = slice.actions;

export default slice.reducer;
