import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';
import { BASE_URL } from '../api';

const initialState = {
  loading:  false,
  teamName: '',
  lobbyId:  ''
};


export const joinGame = createAsyncThunk('quiz/joinGameStatus', async ({ lobbyId, teamName }) => {
    try {
      const { data, status } = await axios.post(`${BASE_URL}/lobbies/${lobbyId}/teams`, { teamName });
      return { data, status };
    } catch ({ data, status }) {
      console.log(data, status)
      return { data, status };
    }
  }
);

export const slice = createSlice({
  name:          'quiz',
  initialState,
  reducers:      {
    setLobbyId:  (state, { payload }) => {
      state.lobbyId = payload;
    },
    setTeamName: (state, { payload }) => {
      state.teamName = payload;
    }
  },
  extraReducers: {
    [joinGame.pending]:   state => {
      state.loading = true;
    },
    [joinGame.fulfilled]: state => {
      state.loading = false;
    },
    [joinGame.rejected]:  state => {
      state.loading = false;
    },
  },
});

export const { setLobbyId, setTeamName } = slice.actions;

export default slice.reducer;
