import { io } from 'socket.io-client';

let ws;

const connect = lobbyId => {
  ws.emit('TEAM_CONNECT', { lobby: lobbyId });
};

const socket = (dispatch, state, history) => {
  if (ws === undefined) {
    ws = io('ws://localhost:3000', { reconnectionDelayMax: 10000, withCredentials: true });
  }

  ws.on('message', ({ type, payload }) => {
    console.log(state);
    switch (type) {
      case 'TEAM_REMOVED':
        if (payload.teamId === state.quiz.teamName) {
          ws.emit('TEAM_LEAVE');
          history.push('/');
        }
        break;
      default:
        console.log(type);
        console.table(payload);
    }
  });
};

export { connect, socket };
