import { io } from 'socket.io-client';
import { resetAll } from './reducers/quiz';


let ws;

const connect = lobbyId => {
  ws.emit('TEAM_CONNECT', { lobby: lobbyId });
};

const socket = (dispatch, state, history) => {
  if (ws === undefined) {
    ws = io('ws://localhost:3000', { reconnectionDelayMax: 10000, withCredentials: true });
  }

  ws.on('message', ({ type, payload }) => {
    switch (type) {
      case 'TEAM_REMOVED':
        if (payload.teamId === state.quiz.teamName) {
          ws.emit('TEAM_DISCONNECT', { lobby: state.quiz.lobbyId });
          dispatch(resetAll());
          history.push('/');
        }
        break;
      case 'NEW_QUESTION':
        // TODO: Go to questions page and show fetch question.
        break;
      case 'LOBBY_ENDED':
        // TODO: Go back to home page.
        break;
      default:
        console.log('Socket.io message:', type, payload);
    }
  });
};

export { connect, socket };
